#include "../../include/utils.h"

vector<std::string> commonChars(vector<std::string>& words) {
    int hash[26];
    memset(hash, 5, sizeof(int) * 26);
    int tmp[26] = {0};

    for (int i = 0; i < words.size(); ++i) {
        std::string word = words[i];
        for (char ch: word) {
            tmp[ch - 'a']++;
        }
        for (int j = 0; j < 26; j++) {
            hash[j] = min(hash[j], tmp[j]);
        }
        memset(tmp, 0, sizeof(int) * 26);
    }

    vector<std::string> res;
    for (int i = 0; i < 26; i++) {
        for (int j = 0; j < hash[i]; ++j) {
            res.push_back(std::string(1, 'a' + i));
        }
    }
    return res;
}

